import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function Signup() {
  const [user_id, setUsername] = useState("");
  const [user_pw, setPassword] = useState("");
//   const [user_pw_confirm, setPasswordConfirm] = useState("");
  const [user_name, setName] = useState("");
  const [user_num, setnum] = useState("");
  const [user_date, setdate] = useState("");
  const navigate = useNavigate();

  const handleSignup = async () => {
    // if (user_pw !== user_pw_confirm) {
    //   alert("비밀번호와 비밀번호 확인이 일치하지 않습니다.");
    //   return;
    // }

    try {
      let baseURL = "";
      if (process.env.NODE_ENV === "development") {
        baseURL = "http://121.139.20.242:5100";
      }

      const response = await axios.post(`${baseURL}/api/register`, {
        user_id: user_id,
        user_pw: user_pw,
        user_name: user_name,
        user_num: user_num,
        user_date: user_date,
      });

      if (response.data.success) {
        alert("회원가입에 성공하였습니다. 로그인 페이지로 이동됩니다.");
        navigate("/login");
      } else {
        alert("회원가입에 실패하였습니다. 다시 시도해주세요.");
      }
    } catch (error) {
        console.error("Signup error:", error);
        if (error.response) {
          console.error("Response data:", error.response.data);
          console.error("Response status:", error.response.status);
          console.error("Response headers:", error.response.headers);
          if (error.response.status === 404) {
            alert("API endpoint not found. Please check the server URL.");
          } else if (error.response.status === 500) {
            alert("Internal server error. Please try again later.");
          } else {
            alert("An error occurred while processing your request.");
          }
        } else if (error.request) {
          console.error("Request data:", error.request);
          alert("No response received from the server. Please check your network connection.");
        } else {
          console.error("Error message:", error.message);
          alert("An unexpected error occurred. Please try again later.");
        }
      }
    };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      handleSignup();
    }
  };

  return (
    <div className="App">
      <h1>Damoa 회원가입 페이지</h1>
      <p>안녕하세요, Damoa 회원가입 페이지입니다.</p>

      <div className="signup-form">
        <input
          type="text"
          placeholder="이름"
          value={user_name}
          onChange={(e) => setName(e.target.value)}
          onKeyDown={handleKeyDown}
        />
        <input
          type="text"
          placeholder="전화번호"
          value={user_num}
          onChange={(e) => setnum(e.target.value)}
          onKeyDown={handleKeyDown}
        />
        <input
          type="text"
          placeholder="생년월일 (YYYY-MM-DD)"
          value={user_date}
          onChange={(e) => setdate(e.target.value)}
          onKeyDown={handleKeyDown}
        />
        <input
          type="text"
          placeholder="아이디"
          value={user_id}
          onChange={(e) => setUsername(e.target.value)}
          onKeyDown={handleKeyDown}
        />
        <input
          type="password"
          placeholder="비밀번호"
          value={user_pw}
          onChange={(e) => setPassword(e.target.value)}
          onKeyDown={handleKeyDown}
        />
        {/* <input
          type="password"
          placeholder="비밀번호 확인"
          value={user_pw_confirm}
          onChange={(e) => setPasswordConfirm(e.target.value)}
          onKeyDown={handleKeyDown}
        /> */}
        <button className="signupbutton" onClick={handleSignup}>
          회원가입
        </button>
      </div>
    </div>
  );
}
