import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import communityData from '../utils/communityData';
import styles from '../css/PostDetail.module.css';

const PostDetail = () => {
    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const noticeId = searchParams.get('notice_id');

    const post = communityData.find((item) => item.id === Number(noticeId)) || {};
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const [replyTo, setReplyTo] = useState(null);
    const [newReply, setNewReply] = useState('');
    const [data, setData] = useState([]);
    const [errorMessage, setErrorMessage] = useState('');

    useEffect(() => {
        const fetchData = async () => {
            try {
                let baseURL = '';
                if (process.env.NODE_ENV === 'development') {
                    baseURL = 'http://121.139.20.242:5100';
                }
                const response = await axios.get(`${baseURL}/api/notice_select`, {
                    notice_id: notice_id,
                });
                if (response.data.valid) {
                    setData(response.data.data);
                } else {
                    setErrorMessage('아이디 또는 비밀번호가 잘못되었습니다.');
                }
            } catch (error) {
                setErrorMessage('데이터베이스 연결이 실패하였습니다.');
            }
        };

        fetchData();
    }, [noticeId]);

    const handleAddComment = () => {
        if (newComment.trim() !== '') {
            const newComments = [...comments, { text: newComment, id: Date.now(), replies: [], parentId: null }];
            setComments(newComments);
            setNewComment('');
        }
    };

    const handleAddReply = (parentId) => {
        if (newReply.trim() !== '') {
            const newComments = [...comments, { text: newReply, id: Date.now(), replies: [], parentId: parentId }];
            setComments(newComments);
            setNewReply('');
            setReplyTo(null);
        }
    };

    const handleReply = (commentId) => {
        setReplyTo(commentId);
    };

    return (
        <div className={styles.postDetailContainer}>
            <h1 className={styles.postTitle}>{data.notice_name}</h1>
            <p className={styles.postMeta}>
                조회수: {post.views} | 작성일: {post.date}
            </p>
            <div className={styles.postContent}>{post.content}</div>
            <div className={styles.commentSection}>
                <h2>댓글</h2>
                <div className={styles.commentInput}>
                    <input
                        type="text"
                        value={newComment}
                        onChange={(e) => setNewComment(e.target.value)}
                        placeholder="댓글을 입력하세요"
                    />
                    <button className={styles.button} onClick={handleAddComment}>
                        댓글 달기
                    </button>
                </div>
                <div className={styles.comments}>
                    {comments
                        .filter((comment) => comment.parentId === null)
                        .map((comment) => (
                            <div key={comment.id} className={styles.comment}>
                                <p>{comment.text}</p>
                                <button className={styles.replyButton} onClick={() => handleReply(comment.id)}>
                                    답글 쓰기
                                </button>
                                {replyTo === comment.id && (
                                    <div className={styles.replyInput}>
                                        <input
                                            type="text"
                                            value={newReply}
                                            onChange={(e) => setNewReply(e.target.value)}
                                            placeholder="답글을 입력하세요"
                                        />
                                        <button className={styles.button} onClick={() => handleAddReply(comment.id)}>
                                            답글 달기
                                        </button>
                                    </div>
                                )}
                                {comments
                                    .filter((reply) => reply.parentId === comment.id)
                                    .map((reply) => (
                                        <div key={reply.id} className={styles.reply}>
                                            <p>{reply.text}</p>
                                        </div>
                                    ))}
                            </div>
                        ))}
                </div>
            </div>
        </div>
    );
};

export default PostDetail;
