import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function Signup() {
  const [user_id, setUsername] = useState("");
  const [user_pw, setPassword] = useState("");
  const [user_pw_confirm, setPasswordConfirm] = useState("");
  const [user_name, setName] = useState("");
  const [user_phone, setPhone] = useState("");
  const [user_birth, setBirth] = useState("");
  const navigate = useNavigate();

  const handleSignup = async () => {
    if (user_pw !== user_pw_confirm) {
      alert("비밀번호와 비밀번호 확인이 일치하지 않습니다.");
      return;
    }

    try {
      let baseURL = "";
      if (process.env.NODE_ENV === "development") {
        baseURL = "http://121.139.20.242:5100";
      }

      const response = await axios.post(`${baseURL}/api/register`, {
        user_id,
        user_pw,
        user_name,
        user_phone,
        user_birth,
      });

      if (response.data.success) {
        // 로컬 스토리지에 사용자 정보 저장
        localStorage.setItem("user_id", user_id);
        localStorage.setItem("user_name", user_name);
        localStorage.setItem("user_phone", user_phone);
        localStorage.setItem("user_birth", user_birth);

        navigate("/");
      } else {
        alert("회원가입에 실패하였습니다. 다시 시도해주세요.");
      }
    } catch (error) {
      alert("데이터베이스 연결이 실패하였습니다.");
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") {
      handleSignup();
    }
  };

  return (
    <div className="App">
      <h1>Damoa 회원가입 페이지</h1>
      <p>안녕하세요, Damoa 회원가입 페이지입니다.</p>

      <div className="signup-form">
        <input
          type="text"
          placeholder="이름"
          value={user_name}
          onChange={(e) => setName(e.target.value)}
          onKeyDown={handleKeyDown}
        />
        <input
          type="text"
          placeholder="전화번호"
          value={user_phone}
          onChange={(e) => setPhone(e.target.value)}
          onKeyDown={handleKeyDown}
        />
        <input
          type="text"
          placeholder="생년월일 (YYYY-MM-DD)"
          value={user_birth}
          onChange={(e) => setBirth(e.target.value)}
          onKeyDown={handleKeyDown}
        />
        <input
          type="text"
          placeholder="아이디"
          value={user_id}
          onChange={(e) => setUsername(e.target.value)}
          onKeyDown={handleKeyDown}
        />
        <input
          type="password"
          placeholder="비밀번호"
          value={user_pw}
          onChange={(e) => setPassword(e.target.value)}
          onKeyDown={handleKeyDown}
        />
        <input
          type="password"
          placeholder="비밀번호 확인"
          value={user_pw_confirm}
          onChange={(e) => setPasswordConfirm(e.target.value)}
          onKeyDown={handleKeyDown}
        />
        <button className="signupbutton" onClick={handleSignup}>
          회원가입
        </button>
      </div>
    </div>
  );
}
