import React, { useState } from 'react';
import axios from 'axios';

const WritingPage = () => {
    const [userName, setUserName] = useState('');
    const [noticeName, setNoticeName] = useState('');
    const [noticeDetail, setNoticeDetail] = useState('');
    const [noticeDate, setNoticeDate] = useState('');
    const [rating, setRating] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        try {
            await axios.post('http://localhost:5100/api/posts', {
                user_name: userName,
                notice_name: noticeName,
                notice_detail: noticeDetail,
                notice_date: noticeDate,
                rating: rating
            });
            
            alert('게시글이 성공적으로 등록되었습니다.');
        } catch (error) {
            console.error('게시글 등록 중 오류 발생:', error);
            alert('게시글 등록 중 오류가 발생했습니다.');
        }
    };

    return (
        <div>
            <h2>새 게시글 작성하기</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    사용자 이름:
                    <input type="text" value={userName} onChange={(e) => setUserName(e.target.value)} />
                </label>
                <label>
                    공지 이름:
                    <input type="text" value={noticeName} onChange={(e) => setNoticeName(e.target.value)} />
                </label>
                <label>
                    공지 내용:
                    <textarea value={noticeDetail} onChange={(e) => setNoticeDetail(e.target.value)} />
                </label>
                <label>
                    공지 날짜:
                    <input type="text" value={noticeDate} onChange={(e) => setNoticeDate(e.target.value)} />
                </label>
                <label>
                    평점:
                    <input type="number" value={rating} onChange={(e) => setRating(e.target.value)} />
                </label>
                <button type="submit">등록</button>
            </form>
        </div>
    );
};

export default WritingPage;
