import { useState } from "react";
import styled from "styled-components";
import style from "../../../css/Rating.module.css";
import ReviewStarRating from "../../../svg/ReviewStarRating";
import { Link, useNavigate } from "react-router-dom";

const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: flex-start;
  flex-grow: 0;
  flex-shrink: 0;
  overflow: hidden;
  gap: 20px;
  padding: 40px;
  border-radius: 10px;
  background: #1a1a1a;
  border: 1px solid #262626;
  margin-right: 30px;
  margin-left: 30px;
`;

const Header = styled.div`
  display: flex;
  justify-content: flex-start;
  align-items: center;
  align-self: stretch;
  flex-grow: 0;
  flex-shrink: 0;
  position: relative;
  gap: 30px;
`;

const Title = styled.p`
  flex-grow: 1;
  width: 479px;
  font-size: 24px;
  font-weight: 600;
  text-align: left;
  color: #999;
`;

const Button = styled.button`
  display: flex;
  justify-content: flex-start;
  align-items: center;
  flex-grow: 0;
  flex-shrink: 0;
  position: relative;
  gap: 4px;
  padding: 12px;
  border-radius: 6px;
  background: #e50000;
  border: 1px solid #262626;
  cursor: pointer;
`;

const ButtonText = styled.p`
  flex-grow: 0;
  flex-shrink: 0;
  font-size: 14px;
  font-weight: 500;
  text-align: left;
  color: #fff;
`;

const ReviewItemContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: flex-start;
  flex-grow: 0;
  flex-shrink: 0;
  width: 400px;
  gap: 16px;
  padding: 30px;
  border-radius: 12px;
  background: #0f0f0f;
  border: 1px solid #262626;
  margin-right: 30px;
  transition: transform 0.5s ease; // 슬라이드 애니메이션
`;

const ReviewTitle = styled.p`
  align-self: stretch;
  flex-grow: 0;
  flex-shrink: 0;
  width: 215px;
  font-size: 18px;
  font-weight: 500;
  text-align: left;
  color: #fff;
`;

const ReviewSubTitle = styled.p`
  align-self: stretch;
  flex-grow: 0;
  flex-shrink: 0;
  width: 215px;
  font-size: 16px;
  font-weight: 500;
  text-align: left;
  color: #999;
  margin-top: -20px;
`;

const ReviewText = styled.p`
  align-self: stretch;
  flex-grow: 0;
  flex-shrink: 0;
  width: 340px;
  font-size: 16px;
  text-align: left;
  color: #999;
`;

const ReviewContainer = styled.div`
  display: flex;
  width: calc(300px * 3); // 세 개의 리뷰 아이템에 대한 총 너비
  transition: transform 0.5s cubic-bezier(0.25, 0.46, 0.45, 0.94); // 부드러운 이징 효과 적용
`;

const IconContainer = styled.div`
  display: flex;
  justify-content: flex-start;
  align-items: center;
  gap: 8px;
`;

const RoundButton = styled.div`
  display: flex;
  justify-content: flex-start;
  align-items: center;
  padding: 12px;
  border-radius: 100px;
  background: #141414;
  border: 1px solid #262626;
  &:hover {
    background-color: #262626; // 호버 시 배경 색상 변경
  }
  &:active {
    background-color: #262626;
    border: 1px solid red;
    transition: all 0.3s ease;
  }
`;

const SvgIcon = styled.svg`
  width: 20px;
  height: 20px;
`;

const ReviewTitleContainer = styled.div`
  display: flex;
`;
export default function Review() {
