import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from '../css/Notice.module.css';

export default function NoticeBox() {
    const [notice, setNotice] = useState('');
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchNotice = async () => {
            try {
                const response = await axios.post('/api/notice_select', { notice_auth: '3' });
                if (response.data.valid) {
                    setNotice(response.data.data[0].notice_content); // Adjust according to your data structure
                } else {
                    setNotice('No notices available.');
                }
            } catch (err) {
                setError('Error fetching notice');
            }
        };

        fetchNotice();
    }, []);

    if (error) {
        return <div className={styles.error}>{error}</div>;
    }

    return (
        <div className={styles.box}>
            <p className={`${styles['text-block']} ${styles.title}`}>공지사항</p>
            <p className={styles['text-block']}>{notice}</p>
        </div>
    );
}
