import React, { useState, useEffect } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import styles from '../css/WritingPage.module.css';

const WritingPage = () => {
    const [isPrivate, setIsPrivate] = useState(false);
    const [content, setContent] = useState('');
    const [title, setTitle] = useState('');
    const [charCount, setCharCount] = useState(0);
    const [error, setError] = useState({ title: '', content: '' });
    const charLimit = 1000;

    const handleSubmit = async (e) => { // Function to handle form submission
        e.preventDefault(); // Preventing default form submission behavior
        
        try {
            // Sending a POST request to the server with user input
            await axios.post('http://localhost:5100/api/posts', {
                user_name: userName,
                notice_name: noticeName,
                notice_detail: noticeDetail,
                notice_date: noticeDate,
                rating: rating
            });
            
            alert('게시글이 성공적으로 등록되었습니다.'); // Showing success message
        } catch (error) {
            console.error('게시글 등록 중 오류 발생:', error); // Logging error to console
            alert('게시글 등록 중 오류가 발생했습니다.'); // Showing error message
        }
    };

    return (
        <div className={styles.writePageContainer}> {/* Container for the entire page */}
            <div className={styles.writeHeader}> {/* Header section */}
                <input
                    type="text"
                    placeholder="제목을 입력해주세요"
                    className={styles.titleInput}
                    value={title}
                    onChange={(e) => {
                        setTitle(e.target.value);
                        validateTitle();
                    }}
                    onBlur={validateTitle}
                /> {/* Input field for title */}
                {error.title && <div className={styles.error}>{error.title}</div>} {/* Error message for title */}
                <div className={styles.privateContainer}> {/* Container for private option */}
                    <input
                        type="checkbox"
                        id="private"
                        checked={isPrivate}
                        onChange={handlePrivateChange}
                        className={styles.privateCheckbox}
                    /> {/* Checkbox for private option */}
                    <label htmlFor="private" className={styles.privateLabel}> {/* Label for private option */}
                        회원만 보기
                    </label>
                </div>
            </div>
            <div className={styles.editorContainer}> {/* Container for the editor */}
                <ReactQuill
                    value={content}
                    onChange={(value) => {
                        handleContentChange(value);
                        validateContent();
                    }}
                    onBlur={validateContent}
                    className={styles.textArea}
                /> {/* Rich text editor for content */}
                <div className={styles.charCount}> {/* Character count */}
                    {charCount}/{charLimit}
                </div>
                {error.content && <div className={styles.error}>{error.content}</div>} {/* Error message for content */}
                <button onClick={handleImageUpload} className={styles.imageUploadButton}> {/* Button to upload image */}
                    이미지 업로드
                </button>
            </div>
            <div className={styles.footer}> {/* Footer section */}
                <button className={styles.cancelButton} onClick={handleCancel}> {/* Button to cancel */}
                    취소
                </button>
                <button className={styles.submitButton} onClick={handleSubmit}> {/* Button to submit */}
                    등록
                </button>
            </div>
        </div>
    );
};

export default WritingPage; // Exporting the component
