import React, { useState, useEffect } from 'react';
import ReactQuill from 'react-quill';
import axios from 'axios';
import 'react-quill/dist/quill.snow.css';
import styles from '../css/WritingPage.module.css';

const WritingPage = () => {
    const [userName, setUserName] = useState('');
    const [noticeName, setNoticeName] = useState('');
    const [noticeDetail, setNoticeDetail] = useState('');
    const [noticeDate, setNoticeDate] = useState('');
    const [rating, setRating] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        
        try {
            await axios.post('http://localhost:5100/api/posts', {
                user_name: userName,
                notice_name: noticeName,
                notice_detail: noticeDetail,
                notice_date: noticeDate,
                rating: rating
            });
            
            alert('게시글이 성공적으로 등록되었습니다.');
        } catch (error) {
            console.error('게시글 등록 중 오류 발생:', error);
            alert('게시글 등록 중 오류가 발생했습니다.');
        }
    };

    return (
        <div className={styles.writePageContainer}>
            <div className={styles.writeHeader}>
                <input
                    type="text"
                    placeholder="제목을 입력해주세요"
                    className={styles.titleInput}
                    value={title}
                    onChange={(e) => {
                        setTitle(e.target.value);
                        validateTitle();
                    }}
                    onBlur={validateTitle}
                />
                {error.title && <div className={styles.error}>{error.title}</div>}
                <div className={styles.privateContainer}>
                    <input
                        type="checkbox"
                        id="private"
                        checked={isPrivate}
                        onChange={handlePrivateChange}
                        className={styles.privateCheckbox}
                    />
                    <label htmlFor="private" className={styles.privateLabel}>
                        회원만 보기
                    </label>
                </div>
            </div>
            <div className={styles.editorContainer}>
                <ReactQuill
                    value={content}
                    onChange={(value) => {
                        handleContentChange(value);
                        validateContent();
                    }}
                    onBlur={validateContent}
                    className={styles.textArea}
                />
                <div className={styles.charCount}>
                    {charCount}/{charLimit}
                </div>
                {error.content && <div className={styles.error}>{error.content}</div>}
                <button onClick={handleImageUpload} className={styles.imageUploadButton}>
                    이미지 업로드
                </button>
            </div>
            <div className={styles.footer}>
                <button className={styles.cancelButton} onClick={handleCancel}>
                    취소
                </button>
                <button className={styles.submitButton} onClick={handleSubmit}>
                    등록
                </button>
            </div>
        </div>
    );
};

export default WritingPage;
