import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from '../css/Notice.module.css';

export default function NoticeBox() {
    const [notice, setNotice] = useState('');
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchNotice = async () => {
            try {
                // Define the base URL
                let baseURL = '';
                if (process.env.NODE_ENV === 'development') {
                    // If in development environment, use local IP
                    baseURL = 'http://121.139.20.242:5100';
                }
                const response = await axios.post(`${baseURL}/api/check`, {
                    user_id: user_id,
                    user_pw: user_pw,
                });
                if (response.data.valid) {
                    localStorage.setItem('user_id', user_id);
                    window.location.reload();
                } else {
                    setErrorMessage('아이디 또는 비밀번호가 잘못되었습니다.');
                }
            } catch (error) {
                setErrorMessage('데이터베이스 연결이 실패하였습니다.');
            }
        };

        fetchNotice();
    }, []);

    if (error) {
        return <div className={styles.error}>{error}</div>;
    }

    return (
        <div className={styles.box}>
            <p className={`${styles['text-block']} ${styles.title}`}>공지사항</p>
            <p className={styles['text-block']}>{notice}</p>
        </div>
    );
}
