import React, { useState } from 'react';
import axios from 'axios';

const YourComponent = () => {
  const [user, setUser] = useState({
    user_id: '',
    user_pw: '',
    user_name: '',
    user_num: '',
    user_date: ''
  });

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    axios.post('http://121.139.20.242:5100/api/register', user)
      .then((response) => {
        console.log(response);
        // 성공적으로 전송된 경우에 대한 처리
      })
      .catch((error) => {
        console.error(error);
        // 오류 처리
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <h1>아이디</h1>
      <input type="text" name="user_id" value={user.user_id} onChange={handleChange} />
      <h1>비번</h1>
      <input type="password" name="user_pw" value={user.user_pw} onChange={handleChange} />
       <h1>이름</h1>
      <input type="text" name="user_name" value={user.user_name} onChange={handleChange} />
      <h1>전번</h1>
      <input type="text" name="user_num" value={user.user_num} onChange={handleChange} />
      <h1>생일</h1>
      <input type="text" name="user_date" value={user.user_date} onChange={handleChange} />
      <button type="submit">Submit</button>
    </form>
  );
};

export default YourComponent;
